import React, { Component } from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox'
import { gundams } from  './gundams';
import './App.css';


class App extends Component{
	constructor(){
		super();
		this.state = {
				gundams: [],
				searchfield: ''
		}
	}

	componentDidMount(){
		this.setState({ gundams: gundams})
	}

	onSearchChange = (event) => {
		this.setState({ searchfield: event.target.value })

	}

	render(){
		const filteredGundams = this.state.gundams.filter(gundams => {
			return gundams.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
		})
		//console.log(filteredRobots);


		return (
		<div className='tc'>
			<h1 className='f1'>Gundam's Infocard</h1>
			<SearchBox searchChange={this.onSearchChange}/>
			<CardList gundams={filteredGundams}/>
		</div>
		);
	}

}

export default App;
